import {  Image, Descriptions } from 'antd';
import React, {useEffect, useState} from 'react'
import { useRouter } from 'next/router'
import { getCookie } from 'cookies-next';
import { useQuery, gql } from '@apollo/client';
import {Layout, Content, contentStyle, Container} from 'src/styles/components.js'
import { MenuBar, AppHeader, AppFooter } from 'src/components/components';

const GET_MOVIE_BY_ID = gql`
  query GetMovieByID($input: GetMovieByIDInput) {
    getMovieByID(input: $input) {
      _movieID
      movie_name
      description
      genres
      movie_duration
      movie_image
    }
}
`;

export const getServerSideProps = ({ req, res }) => {
  const token = getCookie('THEATER_SEAT_BOOKING_COOKIE',{ req, res })
  return (token) ? 
      {
        props: {token : JSON.parse(JSON.stringify(token))} 
      }:
      { props: {}}
};

export default function movieDetail({token}) {
  const router = useRouter()

  const [ movie, setMovie ] = useState({})

  const {data, loading, error, refetch} = useQuery(GET_MOVIE_BY_ID, {
    variables: {input: {_movieID: router.query._movieID}}
  })

  // store fetched data
  useEffect(() => {
    if (data) {
      setMovie(data.getMovieByID)
    }
  }, [data]);


  // refetch data everytime routing to this page
  useEffect(() => {
    const handleRouteChange = () => {
      if (router.pathname === `/movies/${router.query._movieID}`){
        refetch()
      }
    }
    router.events.on('routeChangeComplete', handleRouteChange)
    return () => {
      router.events.off('routeChangeComplete', handleRouteChange)
    }
  }, [router.pathname, refetch])

  if (loading) return <div>Loading...</div>;
  if (error) return `Error! ${error}`;
  return (
  <Container>
    <Layout>
      <AppHeader/>
      <MenuBar router={router}  token={token}/>
      
      <Content
        style={contentStyle}
      >  
      <br/>
        <strong style={{fontSize:"250%"}}>MOVIE DETAIL</strong>
      <br/> 
      <br/> 
      <div style={{display: "flex", justifyContent: "center", margin: " 0px 10px"}}>
        {/* Image */}
        <div style={{margin: "0px 100px 0px 0px"}}>
          {
            (!movie.movie_image) ?
            (
              <Image
                width={300}
                height={400}
                src="error"
                style={{border: "1px solid"}}
                fallback="data:image/png;base64,"
              />
            ):
            (
              <Image
                width={300}
                height={400}
                src={movie.movie_image}
              />
            )
          }
        </div>
        

        {/* Detail */}
        <div style={{maxWidth: '50%'}}>
        <Descriptions column={1} labelStyle={{fontWeight: "800", fontSize: "20px"}} contentStyle={{fontSize: "15px"}} bordered>
          <Descriptions.Item label="NAME">{movie.movie_name?.toUpperCase()}</Descriptions.Item>
          <Descriptions.Item label="GENRES">
            {
              (movie.genres) && (
                movie.genres.reduce((str, genre) => {return str += ` ${genre}`})
              )
            }
          </Descriptions.Item>
          <Descriptions.Item label="DURATION">{movie.movie_duration}</Descriptions.Item>
          <Descriptions.Item label="DESCRIPTION">{movie.description}</Descriptions.Item>
          
        </Descriptions>
        </div> 
        
      </div>

      

      </Content>
    </Layout>
    <AppFooter/>
  </Container>
  )
}

